import axios from "axios";
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./../index.css";

function UserDashboard() {
  const navigate = useNavigate();
  const [snacks, setSnacks] = useState([]);
  const [username, setUsername] = useState("");

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem("user"));
    if (!user || user.userType !== "user") {
      navigate("/");
    } else {
      setUsername(user.username); // Set username
    }

    // Fetch snacks from database
    axios
      .get("http://localhost:5000/api/snacks")
      .then((res) => setSnacks(res.data))
      .catch(() => alert("Failed to load snacks"));
  }, []);

  return (
    <div className="dashboard-container">
      {/* Sidebar */}
      <aside className="sidebar">
        <div className="logo">üç™ Snack Overflow</div>
        <nav>
          <ul>
            <li className="active">üè† Home</li>
            <li>üë§ Profile</li>
            <li onClick={() => { localStorage.clear(); navigate("/"); }}>üö™ Logout</li>
          </ul>
        </nav>
      </aside>

      {/* Main Content */}
      <div className="main-content">
        <header>
          <h1>Welcome Back, <em>{username}</em></h1>
          <div className="cart-icon">üõí</div>
        </header>

        {/* Snacks Grid */}
        <div className="snacks-grid">
          {snacks.map((snack) => (
            <div key={snack.snack_id} className="snack-card">
              <div className="snack-placeholder">{snack.snack_name}</div>
              <button className="add-to-cart">+ Add to cart</button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default UserDashboard;
