import React from 'react';
import SidebarLayout from '../components/SidebarLayout';

function EmployeeProfile() {
  const user = JSON.parse(localStorage.getItem("user"));
  const topSnacks = ["Nature Valley Bar", "Twix Candy Bar", "Lays Barbeque"];

  return (
    <SidebarLayout>
      <h1>{user.username}'s Profile</h1>
      <p><strong>Employee ID:</strong> {user.id}</p>
      <p><strong>Email:</strong> {user.email}</p>
      <button>Forgot Password?</button>
      <p><em>Snack Requests Pending:</em> 1 request pending….</p>
      <p><em>Top 3 Favourite Snacks:</em></p>
      <ul>
        {topSnacks.map((snack, i) => <li key={i}>⭐ {snack}</li>)}
      </ul>
    </SidebarLayout>
  );
}

export default EmployeeProfile;
