import axios from "axios";
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./../index.css";

function OrderHistory() {
  const navigate = useNavigate();
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem("user"));
    if (!user) {
      navigate("/");
      return;
    }

    // Fetch user's order history
    axios.get(`http://localhost:5000/api/orders/${user.id}`)
      .then((res) => setOrders(res.data))
      .catch(() => alert("Failed to load order history"));
  }, []);

  return (
    <div className="dashboard-container">
      {/* Sidebar */}
      <aside className="sidebar">
        <div className="logo">üç™ Snack Overflow</div>
        <nav>
          <ul>
            <li onClick={() => navigate("/dashboard")}>üè† Home</li>
            <li className="active">üìú Order History</li>
            <li>üë§ Profile</li>
            <li onClick={() => { localStorage.clear(); navigate("/"); }}>üö™ Logout</li>
          </ul>
        </nav>
      </aside>

      {/* Main Content */}
      <div className="main-content">
        <header>
          <h1>üìú Your Order History</h1>
        </header>

        <div className="order-list">
          {orders.length > 0 ? (
            orders.map((order) => (
              <div key={order.order_id} className="order-card">
                <p><strong>Order #:</strong> {order.order_number}</p>
                <p><strong>Snack:</strong> {order.snack_name}</p>
                <p><strong>Date:</strong> {new Date(order.date_ordered).toLocaleDateString()}</p>
                <p><strong>Status:</strong> {order.order_status}</p>
              </div>
            ))
          ) : (
            <p>No orders found.</p>
          )}
        </div>
      </div>
    </div>
  );
}

export default OrderHistory;
