import React, { useEffect, useState } from 'react';
import "./../index.css";

function EmployeeProfile() {
  const user = JSON.parse(localStorage.getItem("user"));
  const [pending, setPending] = useState(0);
  const topSnacks = ["Nature Valley Bar", "Twix Candy Bar", "Lays Barbeque"];

  useEffect(() => {
    // Optional: fetch real pending count
    setPending(1);
  }, []);

  return (
    <div className="main-content">
      <h1>{user.username}'s Profile</h1>
      <div className="profile-card">
        <p><strong>Name</strong><br />{user.username}</p>
        <p><strong>Employee ID</strong><br />{user.id}</p>
        <p><strong>Email</strong><br />{user.email}</p>
        <button onClick={() => window.location.href = "/forgot-password"}>Forgot Password?</button>
        <br /><br />
        <p><em>Snack Requests Pending</em><br />{pending} request pending….</p>
        <p><em>Top 3 Favourite Snacks</em></p>
        <ul>
          {topSnacks.map((snack, i) => <li key={i}>⭐ {snack}</li>)}
        </ul>
      </div>
    </div>
  );
}

export default EmployeeProfile;
